class Store {
  constructor() {
    this.state = {};
    this.listeners = [];
  }

  set(newState) {
    this.state = {...this.state, ...newState};
    this.listeners.forEach(listener => listener());
  }

  get() {
    return this.state;
  }

  subscribe(listener) {
    this.listeners.push(listener);
    return () => {
      this.listeners = this.listeners.filter(l => l !== listener);
    }
  }

  unsubscribe(unsubscribeFunction) {
    unsubscribeFunction();
  }
}

