function isMonotonic(numbers) {
    let direction = 0;

    for (let i = 1; i < numbers.length; i++) {
        const diff = numbers[i] - numbers[i - 1];

        if (diff === 0) continue;

        if (direction === 0) {
            direction = diff > 0 ? 1 : -1;
        } else if ((direction === 1 && diff < 0) || (direction === -1 && diff > 0)) {
            return false;
        }
    }

    return true;
}

