import React, { useState, useEffect, useCallback } from 'react';
import TodoInput from './TodoInput';
import TodoItem from './TodoItem';

const generateID = () =>
  `${Date.now().toString(36)}-${Math.random().toString(36).substr(2, 9)}`;

const Todo = () => {
  const [todoItems, setTodoItems] = useState([]);
  const [completedItemIds, setCompletedItemIds] = useState([]);

  // –ó–∞–≥—Ä—É–∑–∫–∞ –∏–∑ localStorage
  useEffect(() => {
    const saved = localStorage.getItem('todos');
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        setTodoItems(parsed.todoItems || []);
        setCompletedItemIds(parsed.completedItemIds || []);
      } catch {
        console.warn('Saved todos non-existent or corrupt.');
      }
    }
  }, []);

  // –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –≤ localStorage
  useEffect(() => {
    localStorage.setItem(
      'todos',
      JSON.stringify({ todoItems, completedItemIds })
    );
  }, [todoItems, completedItemIds]);

  const addTodoItem = useCallback((text) => {
    const newItem = { text, id: generateID() };
    setTodoItems((prev) => [...prev, newItem]);
  }, []);

  const toggleItemCompleted = useCallback((id) => {
    setCompletedItemIds((prev) =>
      prev.includes(id)
        ? prev.filter((itemId) => itemId !== id)
        : [...prev, id]
    );
  }, []);

  return (
    <div className="todo-container">
      {todoItems.map((item) => (
        <TodoItem
          key={item.id}
          {...item}
          completedItemIds={completedItemIds}
          toggleItemCompleted={toggleItemCompleted}
        />
      ))}
      <TodoInput onAdd={addTodoItem} />
    </div>
  );
};

export default Todo;

