    const size = sizeMap.get(node) || node.size || 0;

    console.log(`${tab}${node.name} (${size}kb)`); // –í—ã–≤–æ–¥–∏–º –∏–º—è –∏ —Ä–∞–∑–º–µ—Ä —Å –æ—Ç—Å—Ç—É–ø–æ–º

    // –î–æ–±–∞–≤–ª—è–µ–º –¥–µ—Ç–µ–π –≤ —Å—Ç–µ–∫ –≤ –æ–±—Ä–∞—Ç–Ω–æ–º –ø–æ—Ä—è–¥–∫–µ –¥–ª—è –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–≥–æ –ø–æ—Ä—è–¥–∫–∞ –≤—ã–≤–æ–¥–∞
    if (node.children) {
      for (let i = node.children.length - 1; i >= 0; i--) {
        printStack.push({ node: node.children[i], tab: tab + "  " });
      }
    }
  }
}

