function getRoute(tickets = [], start) {
  const routesMap = {};

  tickets.forEach((item) => {
    const { from, to } = item;
    routesMap[from] = to;
  });

  let currentCity = start;
  const result = [];

  while (currentCity && routesMap[currentCity]) {
    result.push({ from: currentCity, to: routesMap[currentCity] });
    currentCity = routesMap[currentCity];
  }

  return result;
}

