function par(urls, limit, callback) {
  const results = new Array(urls.length);
  let count = 0;
  const cache = new Map();

  async function fn(index) {
    if(index >= urls.length) {
      return
    }

    let resp;

    if(cache.has(urls[index])) {
        resp = cache.get(urls[index])
    } else {
      resp = await urls[index];
      cache.set(urls[index], resp);
    }

    results[index] = resp;
    count++;

    if(count === urls.length) {
      callback(results)
    } else {
      fn(index + limit)
    }
  }

  for (let i = 0; i < limit; i++) fn(i);
}

