function callLimit(fn, limit, callback) {
    let count = 0;

    const wrapped = function(...args) {
        if (count >= limit) {
            if (callback) {
                callback();
            }
            return;
        }

        count++;
        // –í—ã–∑—ã–≤–∞–µ–º callback –∫–æ–≥–¥–∞ —ç—Ç–æ –ø–æ—Å–ª–µ–¥–Ω–∏–π —Ä–∞–∑—Ä–µ—à–µ–Ω–Ω—ã–π –≤—ã–∑–æ–≤
        const result = fn.apply(this, args);
        if (count === limit && callback) {
            callback();
        }
        return result;
    };

    wrapped.reset = function() {
        count = 0;
    };

    return wrapped;
}

