function getRoute(tickets = []) {
  // –°–æ–∑–¥–∞–µ–º –∫–∞—Ä—Ç—É –±–∏–ª–µ—Ç–æ–≤, –≥–¥–µ –∫–ª—é—á - –≥–æ—Ä–æ–¥ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏—è, –∞ –∑–Ω–∞—á–µ–Ω–∏–µ - –º–∞—Å—Å–∏–≤ –±–∏–ª–µ—Ç–æ–≤ –∏–∑ —ç—Ç–æ–≥–æ –≥–æ—Ä–æ–¥–∞
  const ticketMap = {};
