  while (currentCity) {
    // –ë–µ—Ä–µ–º –ø–µ—Ä–≤—ã–π –±–∏–ª–µ—Ç –∏–∑ –º–∞—Å—Å–∏–≤–∞ –±–∏–ª–µ—Ç–æ–≤ –∏–∑ —Ç–µ–∫—É—â–µ–≥–æ –≥–æ—Ä–æ–¥–∞
    const nextTicket = ticketMap[currentCity]?.shift();
