function parallelLimit(urls, limit, callback) {
    const results = [];
    const cache = new Map();
    let currentIndex = 0;
    let activeCount = 0;

    const next = () => {
        if (currentIndex >= urls.length && activeCount === 0) {
            callback(results);
            return;
        }

        while (activeCount < limit && currentIndex < urls.length) {
            const url = urls[currentIndex];
            const idx = currentIndex;
            currentIndex++;

            if (cache.has(url)) {
                results[idx] = cache.get(url);
                next();  // move to the next URL without increasing activeCount
            } else {
                activeCount++;
                fetch(url)
                    .then(res => res.text())  // assuming the response needs to be text, can be modified as needed
                    .then(data => {
                        results[idx] = data;
                        cache.set(url, data);
                    })
                    .catch(err => {
                        results[idx] = err;
                    })
                    .finally(() => {
                        activeCount--;
                        next();
                    });
            }
        }
    };

    next();
}


