function getCurrentSeatLimit(currentHour) {
// –ù–∞—Å—Ç—Ä–æ–π–∫–∏ –∞–∫—Ü–∏–∏
const startHour = 12; // –ê–∫—Ü–∏—è –Ω–∞—á–∏–Ω–∞–µ—Ç—Å—è –≤ 12:00
const endHour = 20; // –ê–∫—Ü–∏—è –∑–∞–∫–∞–Ω—á–∏–≤–∞–µ—Ç—Å—è –≤ 20:00
const totalSeats = 100; // –û–±—â–µ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –º–µ—Å—Ç
// –ê–∫—Ü–∏—è –µ—â–µ –Ω–µ –Ω–∞—á–∞–ª–∞—Å—å
if (currentHour < startHour) {
return totalSeats;
}
// –ê–∫—Ü–∏—è —É–∂–µ –∑–∞–∫–æ–Ω—á–∏–ª–∞—Å—å
if (currentHour >= endHour) {
return 0;
}

// –ü—Ä–æ–≥—Ä–µ—Å—Å –∞–∫—Ü–∏–∏ –æ—Ç 0 (–Ω–∞—á–∞–ª–æ) –¥–æ 1 (–∫–æ–Ω–µ—Ü)
const currentPromotionProgress = (currentHour - startHour) / (endHour
- startHour);
return Math.floor(totalSeats * (1 - Math.pow(currentPromotionProgress,
2)));```
