  return (...args) => {
    if (isCalled) {
      return undefined; // —É–∂–µ –≤—ã–∑—ã–≤–∞–ª–∞—Å—å ‚Äî –Ω–∏—á–µ–≥–æ –Ω–µ –¥–µ–ª–∞–µ–º
