```Array.prototype.groupBy = function (fn) {
  return this.reduce((result, item) => {
    const key = fn(item) // –í—ã—á–∏—Å–ª—è–µ–º –∫–ª—é—á –¥–ª—è —Ç–µ–∫—É—â–µ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
    if (!result[key]) {
      // –ï—Å–ª–∏ —Ç–∞–∫–æ–≥–æ –∫–ª—é—á–∞ –µ—â—ë –Ω–µ—Ç –≤ –æ–±—ä–µ–∫—Ç–µ
      result[key] = [] // –°–æ–∑–¥–∞—ë–º –ø—É—Å—Ç–æ–π –º–∞—Å—Å–∏–≤ –¥–ª—è —ç—Ç–æ–≥–æ –∫–ª—é—á–∞
    }
    result[key].push(item) // –î–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç –≤ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â—É—é –≥—Ä—É–ø–ø—É
    return result
  }, {}) // –ù–∞—á–∏–Ω–∞–µ–º —Å –ø—É—Å—Ç–æ–≥–æ –æ–±—ä–µ–∫—Ç–∞
}```


