```import React from 'react'

function getPeople(name, page = 1, options = {}) {
    return fetch(
        `https://rickandmortyapi.com/api/character?name=${name}&page=${page}`,
        options
    )
        .then((res) => res.json())
}

const debounce = (callback, delay) => {//—ç—Ç–æ –∏–Ω—Ç–µ—Ä–≤—å—é–µ—Ä –¥–∞–ª –¥–ª—è —É—Å–∫–æ—Ä–µ–Ω–∏—è, –Ω–æ –º—ã —Å–≤–æ–π —Ö—É–∫ –Ω–∞–ø–∏—Å–∞–ª–∏ –≤–º–µ—Å—Ç–æ –Ω–µ–≥–æ
    let timeoutId
    return (...args) => {
        clearTimeout(timeoutId)
        timeoutId = setTimeout(() => {
            timeoutId = null
            callback(...args)
        }, delay)
    }
}

export default function App() {
    const [searchCharacters, setSearchCharacters] = React.useState('')
    const [characters, setCharacters] = React.useState([])
    const [error, setError] = React.useState(null)
    const [loading, setLoading] = React.useState(false)
    const debounsedSearchCharacters = useDebounce(searchCharacters, 2000) 

    React.useEffect(() => {
        setLoading(true)
        getPeople(debounsedSearchCharacters)
            .then(({error, results}) => {
                if (error) {
                    setError(error)
                    setCharacters([])
                } else {
                    setCharacters(results)
                    setError(null)
                }
            })
            .catch(e => setError(e.message || 'Unknown error'))
            .finally(() => setLoading(false))
    }, [debounsedSearchCharacters])

    return <div>
        <input value={searchCharacters} onChange={(e) => setSearchCharacters(e.target.value)} />
        {error && <div>{error}</div>}
        {loading ? <div>Loading</div> : <ul>
            {characters.map(({ id, name }) => (
                <li key={id}>{name}</li>
            ))}
        </ul>}

    </div>
}

const useDebounce = (value, timer) => {
    const [delayedValue, setDelayedValue] = React.useState(value)

    React.useEffect(() => {
        let timeout = setTimeout(() => setDelayedValue(value), timer)
        return () => clearTimeout(timeout)        
    }, [value])

    return delayedValue
}```

