```function solution(connect, render) {
    // –•—Ä–∞–Ω–∏–º —Å–æ–æ–±—â–µ–Ω–∏—è –≤ Map, –≥–¥–µ –∫–ª—é—á - id —Å–æ–æ–±—â–µ–Ω–∏—è, –∑–Ω–∞—á–µ–Ω–∏–µ - —Å–∞–º–æ —Å–æ–æ–±—â–µ–Ω–∏–µ
    const messages = new Map();
    // –û–∂–∏–¥–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ —Å —ç—Ç–∏–º id —Å–ª–µ–¥—É—é—â–∏–º
    let nextExpectedId = 1;

    connect(function onMessage(message) {
        // –î–æ–±–∞–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ Map
        messages.set(message.id, message);

        // –ü–æ–∫–∞ –µ—Å—Ç—å –æ–∂–∏–¥–∞–µ–º–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ, —Ä–µ–Ω–¥–µ—Ä–∏–º –µ–≥–æ –∏ —É–≤–µ–ª–∏—á–∏–≤–∞–µ–º nextExpectedId
        while (messages.has(nextExpectedId)) {
            render(messages.get(nextExpectedId));
            messages.delete(nextExpectedId);
            nextExpectedId++;
        }
    });
}```
