```type Subscriber<T> = (value: T) => void;

class Subscription<T> {
  private subscribers: Set<Subscriber<T>> = new Set();

  // –î–æ–±–∞–≤–∏—Ç—å –ø–æ–¥–ø–∏—Å—á–∏–∫–∞
  subscribe(callback: Subscriber<T>): void {
    this.subscribers.add(callback);
  }

  // –£–¥–∞–ª–∏—Ç—å –ø–æ–¥–ø–∏—Å—á–∏–∫–∞
  unsubscribe(callback: Subscriber<T>): void {
