 * @param {string} s - –≤—Ö–æ–¥–Ω–∞—è —Å—Ç—Ä–æ–∫–∞
 * @return {number} - –¥–ª–∏–Ω–∞ —Å–∞–º–æ–π –¥–ª–∏–Ω–Ω–æ–π —É–Ω–∏–∫–∞–ª—å–Ω–æ–π –ø–æ–¥—Å—Ç—Ä–æ–∫–∏
 */
function lengthOfLongestSubstring(s) {
    let max = 0; 
    let set = new Set();
    let leftNum = 0;
    for (let current = 0; current < s.length; current++ ) {
        // —É–¥–∞–ª—è–µ–º —Å–∏–º–≤–æ–ª—ã –∏–∑ –Ω–∞—á–∞–ª–∞ –æ–∫–Ω–∞, –ø–æ–∫–∞ –Ω–µ —É–±–µ–æ—Ä–µ–º –¥—É–±–ª–∏–∫–∞—Ç
       while (set.has(s[current])) {
            set.delete(s[leftNum]);
            leftNum++;
        }
        set.add(s[current]);
        // –æ–±–Ω–æ–≤–ª—è–µ–º –º–∞–∫—Å–∏–º–∞–ª—å–Ω—É—é –¥–ª–∏–Ω–Ω—É
        max = Math.max(max, current - leftNum +1)
    }
    return max;
}
