function group(products) {
  const result = { cheap: [], medium: [], expensive: [] };
  products.forEach((product) => {
    const price = product.price;

    if (price <= 150) {
      result.cheap.push(product);
    } else if (price <= 500) {
      result.medium.push(product);
    } else {
      result.expensive.push(product);
    }
  });
  return result;

  // –ü–µ—Ä–µ–ø–∏—Å–∞—Ç—å –Ω–∞ reduce
  return products.reduce((acc, item) => {
    if (item.price <= 150) {
        acc.cheap.push(item);
  } else if (item.price >= 151 && item.price <= 500) {
      acc.medium.push(item);
  } else {
      acc.expensive.push(item);
  }
  return acc;
}, result);
}

// –ü–µ—Ä–µ–ø–∏—Å–∞—Ç—å –∏—Å–ø–æ–ª—å–∑—É—è –Ω–æ–≤—ã–π –º–µ—Ç–æ–¥ groupBy (–¥–∞—ë—Ç –ø–æ—Å–º–æ—Ç—Ä–µ—Ç—å –¥–æ–∫—É)

const result = Object.groupBy(products, (product) => {
