// –ü—Ä–∏ –∫–∞–∂–¥–æ–º –ø–æ—Å–ª–µ–¥—É—é—â–µ–º –≤—ã–∑–æ–≤–µ –æ–Ω–∞ –¥–æ–ª–∂–Ω–∞ –≤–æ–∑–≤—Ä–∞—â–∞—Ç—å undefined.
function once(fn) {
    let isCalled = false;

    return (...args) => {
        if (!isCalled) {
            isCalled = true;
            return fn(...args);
        }
    };
}

