```function parse(csv) {
  const [header, ...data] = csv.trim().split('\n').filter(line => line.trim());
  if (!header) return [];

  const keys = header.split(',');

  return data.map(line => {
    const values = line.split(',');
    return keys.reduce((obj, key, index) => {
      obj[key] = values[index] || '';
      return obj;
    }, {});
  });
}```

