class Dispatcher {
  constructor() {
    // –•—Ä–∞–Ω–∏–ª–∏—â–µ –≤—Å–µ—Ö —Å–æ–±—ã—Ç–∏–π –∏ –∏—Ö –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–æ–≤
    this.events = {};
