  // –ú–µ—Ç–æ–¥ –¥–ª—è –≤—ã–∑–æ–≤–∞ –≤—Å–µ—Ö –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–æ–≤ —Å–æ–±—ã—Ç–∏—è
  trigger(eventName) {
    const handlers = this.events[eventName];
    if (handlers) {
      handlers.forEach((handler) => handler());
    }
  }
}

