```/**
 * –†–µ–∞–ª–∏–∑–æ–≤–∞—Ç—å –∫–ª–∞—Å—Å Subscription.
 * –î–æ–ª–∂–Ω—ã –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞—Ç—å—Å—è –º–µ—Ç–æ–¥—ã subscribe, unsubscribe –∏ next.
 * –í—Å–µ –º–µ—Ç–æ–¥—ã –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å —Å—Ç—Ä–æ–≥–æ —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω—ã.
 */
type HandlerType<T> = (value: T) => void

class Subscription<T> {
  subscriber: Set<HandlerType<T>> = new Set()
  subscribe(handler : HandlerType<T>) {
    this.subscriber.add(handler)
    return ()=> this.unsubscribe1(handler)
  }
  unsubscribe1(handler: HandlerType<T>) {
    this.subscriber.delete(handler )
  }
  next(value: T) {
    this.subscriber.forEach(handler => handler(value))
  }
}

const sub = new Subscription<string>();

const handler1 = (value: string) => {
  console.log('Handler1 received:', value);
};
const handler2 = (value: string) => {
  console.log('Handler2 received:', value);
};

const unsubscribe1 = sub.subscribe(handler1);
const unsubscribe2 = sub.subscribe(handler2);

sub.next('First');
// –í—ã–≤–æ–¥:
// Handler1 received: First
// Handler2 received: First

unsubscribe1();

sub.next('Second');
// –í—ã–≤–æ–¥:
// Handler2 received: Second```
