const throttle: Throttle = (func, delay) => {
  let timer: NodeJS.Timeout | null = null;
  return (...args) => {
    if (!timer) {
      func(...args);
      timer = setTimeout(() => {
        timer = null;
      }, delay);
    }
  };
};

