function callLimit(fn, limit, callback) {
  let count = 0 // –°—á—ë—Ç—á–∏–∫ –≤—ã–∑–æ–≤–æ–≤

  // –û–±—ë—Ä—Ç–∫–∞ –Ω–∞–¥ –∏—Å—Ö–æ–¥–Ω–æ–π —Ñ—É–Ω–∫—Ü–∏–µ–π
  function wrapper(...args) {
    if (count >= limit) return // –ü—Ä–µ–∫—Ä–∞—Ç–∏—Ç—å –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–µ, –µ—Å–ª–∏ –ª–∏–º–∏—Ç –ø—Ä–µ–≤—ã—à–µ–Ω

    count++ // –£–≤–µ–ª–∏—á–∏—Ç—å —Å—á—ë—Ç—á–∏–∫

    // –ï—Å–ª–∏ –≤—ã–∑–æ–≤ –¥–æ—Å—Ç–∏–≥ –ª–∏–º–∏—Ç–∞ ‚Äî –≤—ã–∑–≤–∞—Ç—å callback (–µ—Å–ª–∏ –æ–Ω –ø–µ—Ä–µ–¥–∞–Ω)
    if (count === limit && typeof callback === 'function') {
      callback()
    }

    // –í—ã–∑–æ–≤ –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω–æ–π —Ñ—É–Ω–∫—Ü–∏–∏ —Å –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã–º–∏ –∞—Ä–≥—É–º–µ–Ω—Ç–∞–º–∏
    return fn.apply(this, args)
  }

  // –ú–µ—Ç–æ–¥ –¥–ª—è —Ä—É—á–Ω–æ–≥–æ —Å–±—Ä–æ—Å–∞ —Å—á—ë—Ç—á–∏–∫–∞ –≤—ã–∑–æ–≤–æ–≤
  wrapper.reset = function () {
    count = 0
  }

  return wrapper // –í–µ—Ä–Ω—É—Ç—å –æ–±—ë—Ä–Ω—É—Ç—É—é —Ñ—É–Ω–∫—Ü–∏—é
}
```

