```function flattenTree(nodes) {
    const result = [];

    function recurse(node) {
      // –°–æ–∑–¥–∞–µ–º –∫–æ–ø–∏—é —É–∑–ª–∞ –±–µ–∑ children (–µ—Å–ª–∏ –Ω—É–∂–Ω–æ)
      const { children, ...rest } = node;
      result.push(rest);
      if (children && Array.isArray(children)) {
        children.forEach(child => recurse(child));
      }
    }

