class Buffer {
  constructor (max_size) {
    this.max_size = max_size
    this.buffer = new Array(max_size)
    this.oldest_el = 0
  }

  write (item) {
    if (this.max_size === this.buffer.length) {
      this.buffer[this.oldest_el] = item
      if (this.oldest_el === this.buffer.length) {
        this.oldest_el = 0
        return
      }
      this.oldest_el = this.oldest_el + 1
    } else {
      this.buffer.push(item)
    }
  }

  read () {
    const item = this.buffer[this.oldest_el]
    this.buffer.delete(item)
    this.buffer = this.buffer.filter((el) => Boolean(el))
    return item
  }
}```
