function getRanges(arr) {
  const minRangeLength = 3;
  if (!arr.length) return "";

  const sorted = [...new Set(arr)].sort((a, b) => a - b);
  const result = [];
  let start = sorted[0];
  let count = 1;

  for (let i = 1; i <= sorted.length; i++) {
    if (sorted[i] === sorted[i - 1] + 1) {
      count++;
    } else {
      if (count >= minRangeLength) {
        result.push(`${start}-${sorted[i - 1]}`);
      } else {
        for (let j = start; j <= sorted[i - 1]; j++) {
          result.push(j.toString());
        }
      }
      start = sorted[i];
      count = 1;
    }
  }

  return result.join(", ");
}```
