–æ—Ç–≤–µ—Ç:
```function printDirectoryStructure(data) {
  const sizeMap = new Map();

  // === –ü–µ—Ä–≤—ã–π –ø—Ä–æ—Ö–æ–¥: —Å—á–∏—Ç–∞–µ–º —Ä–∞–∑–º–µ—Ä—ã –≤—Å–µ—Ö —É–∑–ª–æ–≤ (—Ñ–∞–π–ª–æ–≤ –∏ –ø–∞–ø–æ–∫) ===
  const stack = [{ node: data, visited: false }];

  while (stack.length > 0) {
    const { node, visited } = stack.pop();

    if (!node.children) {
      // –§–∞–π–ª ‚Äî –ø—Ä–æ—Å—Ç–æ —Å–æ—Ö—Ä–∞–Ω—è–µ–º —Ä–∞–∑–º–µ—Ä
      sizeMap.set(node, node.size || 0);
    } else if (!visited) {
      // –°–Ω–∞—á–∞–ª–∞ –∫–ª–∞–¥—ë–º –ø–∞–ø–∫—É –æ–±—Ä–∞—Ç–Ω–æ —Å visited: true, —á—Ç–æ–±—ã –ø–æ—Å—á–∏—Ç–∞—Ç—å —Ä–∞–∑–º–µ—Ä –ø–æ—Å–ª–µ –æ–±—Ä–∞–±–æ—Ç–∫–∏ –¥–µ—Ç–µ–π
      stack.push({ node, visited: true });

      // –î–æ–±–∞–≤–ª—è–µ–º –¥–µ—Ç–µ–π –≤ —Å—Ç–µ–∫ –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏
      for (let i = node.children.length - 1; i >= 0; i--) {
        stack.push({ node: node.children[i], visited: false });
      }
    } else {
      // –í—Å–µ –¥–µ—Ç–∏ –æ–±—Ä–∞–±–æ—Ç–∞–Ω—ã ‚Äî —Å—á–∏—Ç–∞–µ–º —Å—É–º–º—É –∏—Ö —Ä–∞–∑–º–µ—Ä–æ–≤
      let totalSize = 0;
      for (const child of node.children) {
        totalSize += sizeMap.get(child) || 0;
      }
      sizeMap.set(node, totalSize);
    }
  }

  // === –í—Ç–æ—Ä–æ–π –ø—Ä–æ—Ö–æ–¥: –ø–µ—á–∞—Ç–∞–µ–º —Å—Ç—Ä—É–∫—Ç—É—Ä—É —Å–≤–µ—Ä—Ö—É –≤–Ω–∏–∑ ===
  const printStack = [{ node: data, tab: "" }];

  while (printStack.length > 0) {
    const { node, tab } = printStack.pop();
    const size = sizeMap.get(node) || node.size || 0;

    console.log(`${tab}${node.name} (${size}kb)`); // –í—ã–≤–æ–¥–∏–º –∏–º—è –∏ —Ä–∞–∑–º–µ—Ä —Å –æ—Ç—Å—Ç—É–ø–æ–º

    // –î–æ–±–∞–≤–ª—è–µ–º –¥–µ—Ç–µ–π –≤ —Å—Ç–µ–∫ –≤ –æ–±—Ä–∞—Ç–Ω–æ–º –ø–æ—Ä—è–¥–∫–µ –¥–ª—è –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–≥–æ –ø–æ—Ä—è–¥–∫–∞ –≤—ã–≤–æ–¥–∞
    if (node.children) {
      for (let i = node.children.length - 1; i >= 0; i--) {
        printStack.push({ node: node.children[i], tab: tab + "  " });
      }
    }
  }
}```

