```const atm = (nominals, amount, limits) => {
  // 1) –°–æ–±–∏—Ä–∞–µ–º –º–∞—Å—Å–∏–≤ { nominal, limit } –∏ —Å–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ —É–±—ã–≤–∞–Ω–∏—é –Ω–æ–º–∏–Ω–∞–ª–∞
  const cash = nominals
    .map((nominal, i) => ({ nominal, limit: limits[i] || 0 }))
    .sort((a, b) => b.nominal - a.nominal);

  let remaining = amount;
  const result = [];

  // 2) –ñ–∞–¥–Ω–æ –±–µ—Ä—ë–º –ø–æ –∫–∞–∂–¥–æ–º—É –Ω–æ–º–∏–Ω–∞–ª—É, –Ω–æ –Ω–µ –±–æ–ª—å—à–µ —á–µ–º –≤ limit
  for (const { nominal, limit } of cash) {
    if (remaining <= 0) break;

    const need = Math.floor(remaining / nominal);
    const take = Math.min(need, limit);

    if (take > 0) {
      result.push(`${nominal} x ${take}`);
      remaining -= nominal * take;
    }
  }

  // 3) –ü—Ä–æ–≤–µ—Ä—è–µ–º, —É–¥–∞–ª–æ—Å—å –ª–∏ —Ç–æ—á–Ω–æ –≤—ã–¥–∞—Ç—å —Å—É–º–º—É
  if (remaining !== 0) {
    throw new Error(
      `–ù–µ–≤–æ–∑–º–æ–∂–Ω–æ –≤—ã–¥–∞—Ç—å —Å—É–º–º—É ${amount}. –û—Å—Ç–∞—Ç–æ–∫ ${remaining} –Ω–µ –æ—Ö–≤–∞—á–µ–Ω –∏–º–µ—é—â–∏–º–∏—Å—è –∫—É–ø—é—Ä–∞–º–∏.`
    );
  }

  return result;
};```

