    return fn(...args); // –≤—ã–∑—ã–≤–∞–µ–º fn —Ç–æ–ª—å–∫–æ –æ–¥–∏–Ω —Ä–∞–∑
  };
}```
2. –ù–∞–ø–∏—à–∏ —Ñ—É–Ω–∫—Ü–∏—é, –∫–æ—Ç–æ—Ä–∞—è –ø—Ä–∏–Ω–∏–º–∞–µ—Ç –Ω–µ—Å–∫–æ–ª—å–∫–æ –ø—Ä–æ–º–∏—Å–æ–≤ –∏ –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç –ø—Ä–æ–º–∏—Å, –≤ –∫–æ—Ç–æ—Ä–æ–º –±—É–¥–µ—Ç —Å—É–º–º–∞ –≤—Å–µ—Ö —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤ —ç—Ç–∏—Ö –ø—Ä–æ–º–∏—Å–æ–≤
```function sumFulfilledPromises(...promises) {
  return Promise.allSettled(promises)
    .then(results => {
      const successValues = results
        .filter(result => result.status === 'fulfilled')
        .map(result => result.value);

      return successValues.reduce((sum, num) => sum + num, 0);
    });
}```
