import React, { useState, useEffect, useRef } from "react";

type NoteEditorProps = {
  initialText: string;
  onSave: (value: string) => void;
};

export function NoteEditor({ initialText, onSave }: NoteEditorProps) {
  const [text, setText] = useState(initialText);
  const debounceTimer = useRef<number | null>(null); // –¥–æ–±–∞–≤–∏–ª

  // Debounce –ª–æ–≥–∏–∫–∞
  useEffect(() => {
