// - batchFetch –≤—Å–µ–≥–¥–∞ —É—Å–ø–µ—à–µ–Ω

function createSmartFetch(timeout) {
  let map = new Map();
  let timer = null;
  return function smartFetch(id) {
    return new Promise((resolve) => {
      if (!map.has(id)) {
        map.set(id, []);
      }
      map.get(id).push(resolve);

      if (!timer) {
        timer = setTimeout(() => {
          const ids = Array.from(map.keys);
          const curMap = new Map(map);
          map.clear();
          timer = null;

          batchFetch(ids).then((results) => {
            for (const id of ids) {
              const resolvers = curMap.get(id);
              for (const r of resolvers) {
                r(results[id]);
              }
            }
          });
        }, timeout);
        timeout;
      }
    });
  };
}

