function fetchWithRetries(retriesCount) {
    let currentRetries = 0;

    const respFetch = async (url) => {
        currentRetries++;

        try {
            console.log(`Fetch attempt ${currentRetries}`);
            const response = await fetch(url);

            if (!response.ok) {
                throw new Error('Request failed');
            }

            const data = await response.json(); // –î–æ–±–∞–≤–ª–µ–Ω await
