```const PasswordStrength = ({ value }) => {
  const [complexity, setComplexity] = useState(0);

  useEffect(() => {
    // –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω—ã–µ —Ä–µ–≥—É–ª—è—Ä–Ω—ã–µ –≤—ã—Ä–∞–∂–µ–Ω–∏—è (–±–µ–∑ .test(value) –≤–Ω—É—Ç—Ä–∏ —É—Å–ª–æ–≤–∏–π)
    if (value.length === 0) {
      setComplexity(0);
    } else if (/\d/.test(value) && /[a-z]/.test(value)) {
      setComplexity(1);
    } else if (/\d/.test(value) && /[a-zA-Z]/.test(value)) {
      setComplexity(2);
    } else if (/\d/.test(value) && /[A-Z]/.test(value) && /\W/.test(value)) {
      setComplexity(3);
    } else {
      setComplexity(0);
    }
  }, [value]);

  return (
    <div className={`strong-item easy ${complexity > 0 ? "active" : ""}`}>
      {complexity === 1 && "–ü—Ä–æ—Å—Ç–æ–π –ø–∞—Ä–æ–ª—å"}
      {complexity === 2 && "–°—Ä–µ–¥–Ω–∏–π –ø–∞—Ä–æ–ª—å"}
      {complexity === 3 && "–°–ª–æ–∂–Ω—ã–π –ø–∞—Ä–æ–ª—å"}
      {complexity === 0 && "–í–≤–µ–¥–∏—Ç–µ –ø–∞—Ä–æ–ª—å"}
    </div>
  );
};```

