```function mergeSortedArrays(arr1, arr2) {
  const merged = []; // –ò—Ç–æ–≥–æ–≤—ã–π –º–∞—Å—Å–∏–≤ –¥–ª—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
  let i = 0; // –ò–Ω–¥–µ–∫—Å –¥–ª—è –ø–µ—Ä–≤–æ–≥–æ –º–∞—Å—Å–∏–≤–∞
  let j = 0; // –ò–Ω–¥–µ–∫—Å –¥–ª—è –≤—Ç–æ—Ä–æ–≥–æ –º–∞—Å—Å–∏–≤–∞

  // –ü–æ–∫–∞ –Ω–µ –¥–æ—à–ª–∏ –¥–æ –∫–æ–Ω—Ü–∞ –æ–¥–Ω–æ–≥–æ –∏–∑ –º–∞—Å—Å–∏–≤–æ–≤
  while (i < arr1.length && j < arr2.length) {
    // –°—Ä–∞–≤–Ω–∏–≤–∞–µ–º —Ç–µ–∫—É—â–∏–µ —ç–ª–µ–º–µ–Ω—Ç—ã –¥–≤—É—Ö –º–∞—Å—Å–∏–≤–æ–≤
    if (arr1[i] < arr2[j]) {
      // –ï—Å–ª–∏ —ç–ª–µ–º–µ–Ω—Ç –∏–∑ –ø–µ—Ä–≤–æ–≥–æ –º–∞—Å—Å–∏–≤–∞ –º–µ–Ω—å—à–µ, –¥–æ–±–∞–≤–ª—è–µ–º –µ–≥–æ –≤ merged
      merged.push(arr1[i]);
      i++; // –¥–≤–∏–≥–∞–µ–º—Å—è –¥–∞–ª—å—à–µ –ø–æ –ø–µ—Ä–≤–æ–º—É –º–∞—Å—Å–∏–≤—É
    } else {
      // –ò–Ω–∞—á–µ –¥–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç –∏–∑ –≤—Ç–æ—Ä–æ–≥–æ –º–∞—Å—Å–∏–≤–∞
      merged.push(arr2[j]);
      j++; // –¥–≤–∏–≥–∞–µ–º—Å—è –¥–∞–ª—å—à–µ –ø–æ –≤—Ç–æ—Ä–æ–º—É –º–∞—Å—Å–∏–≤—É
    }
  }
  return merged; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –æ–±—ä–µ–¥–∏–Ω—ë–Ω–Ω—ã–π –æ—Ç—Å–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –º–∞—Å—Å–∏–≤
}```

