```function isSimilar(arr1, arr2) {
    // –ï—Å–ª–∏ –¥–ª–∏–Ω—ã –º–∞—Å—Å–∏–≤–æ–≤ –Ω–µ —Å–æ–≤–ø–∞–¥–∞—é—Ç ‚Äî –æ–Ω–∏ –Ω–µ –ø–æ—Ö–æ–∂–∏
    if (arr1.length !== arr2.length) {
        return false;
    }
    const countMap = {};

    for (let item of arr1) {
        countMap[item] = (countMap[item] || 0) + 1;
    }
    for (let item of arr2) {
        if (!countMap[item]) {
            return false;
        }
        countMap[item] -= 1;
    }
    return true;
}```

