import React from "react";
import {useState} from 'react' 

export default () => {
    const [kilometers, setKilometers] = useState('')
    const [miles, setMiles] = useState('')

    const convertToMiles = (km) => {
        if (km === '' || isNaN(km)) return ''
        return (parseFloat(km) * 0.6).toFixed(2)
    }
    const convertToKm = (mi) => {
        if (mi === '' || isNaN(mi)) return ''
        return (parseFloat(mi) * 1.6).toFixed(2)
    }
    const handleKilometers = (e) => {
        const km = e.target.value
        setKilometers(km)
        setMiles(convertToKm(km))

    }
    const handleMiles = (e) => {
        const mi = e.target.value
        setMiles(mi)
        setKilometers(convertToMiles(mi))

    }


    return (
        <div>
            <p>KM:{kilometers}</p>
            <p>MI:{miles}</p>
            <input type='number' value={kilometers} onChange={handleKilometers} placeholder = {'KM'} />
            <input type='number' value={miles} onChange={handleMiles} placeholder = {"Miles"} />

        </div>
    )


}```

