    // –ï—Å–ª–∏ —ç—Ç–æ –Ω–µ –æ–±—ä–µ–∫—Ç –∏–ª–∏ null, –ø—Ä–æ—Å—Ç–æ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏–µ
    return obj;
  }

  if (Array.isArray(obj)) {
    // –ï—Å–ª–∏ —ç—Ç–æ –º–∞—Å—Å–∏–≤, —Ä–µ–∫—É—Ä—Å–∏–≤–Ω–æ –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –∫–∞–∂–¥—ã–π —ç–ª–µ–º–µ–Ω—Ç
    return obj.map(snakeToCamel);
  }

  // –ï—Å–ª–∏ —ç—Ç–æ –æ–±—ä–µ–∫—Ç, —Å–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π –æ–±—ä–µ–∫—Ç —Å –ø—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–Ω—ã–º–∏ –∫–ª—é—á–∞–º–∏
  const result = {};
  for (const key in obj) {
    if (obj.hasOwnProperty(key)) {
      // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –∫–ª—é—á –∏–∑ snake_case –≤ camelCase
      const newKey = key.replace(/_([a-z])/g, (_, letter) => letter.toUpperCase());
      // –†–µ–∫—É—Ä—Å–∏–≤–Ω–æ –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏–µ
      result[newKey] = snakeToCamel(obj[key]);
    }
  }

  return result;
}```
