```function rateLimiter(fn, limit, interval) {
  const calls = [];

  return function (...args) {
    const now = Date.now();
    calls.push(now);

    if (calls.length <= limit) {
      return fn(...args);
    }

    const nthPrevCall = calls[calls.length - limit - 1];
    if (now - nthPrevCall > interval) {
      return fn(...args);
    }

    // –£–±–∏—Ä–∞–µ–º –ø–æ—Å–ª–µ–¥–Ω–∏–π –≤—ã–∑–æ–≤, —Ç.–∫. –æ–Ω –Ω–µ –¥–æ–ª–∂–µ–Ω –∑–∞—Å—á–∏—Ç–∞—Ç—å—Å—è
    calls.unshift();
    return null; // –ª–∏–º–∏—Ç –ø—Ä–µ–≤—ã—à–µ–Ω
  };
}```


