// –ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è
const CONFIG = {
  CURRENCY: '$',
  API_URL: 'https://my-json-server.typicode.com/cyberwalrus/demo'
};

// –ö–∞—Å—Ç–æ–º–Ω—ã–π —Ö—É–∫ –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö
const useFetchData = <T,>(endpoint: string) => {
  const [data, setData] = useState<T[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${CONFIG.API_URL}/${endpoint}`);
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        const json = await response.json();
        setData(json);
      } catch (err) {
        setError(err as Error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [endpoint]);

  return { data, loading, error };
};

interface TimerProps {
  initial?: number;
}

// 2. –ò—Å–ø–æ–ª—å–∑—É–µ–º TimerProps –¥–ª—è –ø—Ä–æ–ø—Å–æ–≤ ============================
const Timer = ({ initial = 10 }: TimerProps) => {
  const [time, setTime] = useState(initial);

  useEffect(() => {
    const interval = setInterval(() => {
      setTime(prev => prev > 0 ? prev - 1 : 0);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  return <div className="Controls">{time}</div>;
};

interface ShopsItemsProps {
  productId: string;
}
// 3. –ò—Å–ø–æ–ª—å–∑—É–µ–º ShopsItemsProps –¥–ª—è –ø—Ä–æ–ø—Å–æ–≤ =======================
const ShopsItems = ({ productId }: ShopsItemsProps) => {
  const { data: shops, loading, error } = useFetchData<Shop>('shops');

  const filteredShops = useMemo(() =>
    shops.filter(shop => shop.priceList[productId]),
    [shops, productId]
  );

  if (loading) return <div>Loading shops...</div>;
  if (error) return <div>Error loading shops: {error.message}</div>;

  return (
    <ul className="postList">
      {filteredShops.map(({ name, priceList, id: shopId }) => (
        <li className="post__header" key={shopId}>
          {name} - {priceList[productId]}{CONFIG.CURRENCY}
        </li>
      ))}
    </ul>
  );
};

const ProductList = () => {
  const { data: products, loading, error } = useFetchData<Product>('products');

  if (loading) return <div>Loading products...</div>;
  if (error) return <div>Error loading products: {error.message}</div>;```

