```class MaxStack {
constructor() {
    this.stack = []
    this.maxStack = [-Infinity]
}

push(elem) {
    this.stack.push(elem)
    const currentMax = this.maxStack[this.maxStack.length - 1]
    if (elem >= currentMax){
    this.maxStack.push(elem)
    }
}

pop() {
    const poppedElem = this.stack.pop()
    if (poppedElem === this.maxStack.at(-1)) {
    this.maxStack.pop()
    }
    return poppedElem
}

max() {
    return this.maxStack[this.maxStack.length - 1]
}
}

let s = new MaxStack();
console.log(s.max());
// -Infinity
console.log(s.pop());
