```import React, { useState } from "react";

export default function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [filter, setFilter] = useState("all"); // 'all' | 'completed' | 'active'

  // ‚úÖ –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –∑–∞–¥–∞—á–∏
  const handleAdd = () => {
    if (inputValue.trim() === "") return;
    const newTask = {
      id: Date.now(),
      text: inputValue,
      completed: false,
    };
    setTasks([...tasks, newTask]);
    setInputValue(""); // –æ—á–∏—Å—Ç–∫–∞ –ø–æ–ª—è
  };

  // ‚úÖ –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ —Å—Ç–∞—Ç—É—Å–∞ –∑–∞–¥–∞—á–∏
  const handleToggle = (id) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  // ‚úÖ –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –ø–æ –≤—ã–±—Ä–∞–Ω–Ω–æ–º—É —Ñ–∏–ª—å—Ç—Ä—É
  const filteredTasks = tasks.filter((task) => {
    if (filter === "completed") return task.completed;
    if (filter === "active") return !task.completed;
    return true; // 'all'
  });

  return (
    <div>
      <h1>–°–ø–∏—Å–æ–∫ –∑–∞–¥–∞—á</h1>
      <input
        type="text"
        placeholder="–ù–æ–≤–∞—è –∑–∞–¥–∞—á–∞"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
      />
      <button onClick={handleAdd}>–î–æ–±–∞–≤–∏—Ç—å</button>

      <div>
        <button onClick={() => setFilter("all")}>–í—Å–µ</button>
        <button onClick={() => setFilter("active")}>–ê–∫—Ç–∏–≤–Ω—ã–µ</button>
        <button onClick={() => setFilter("completed")}>–í—ã–ø–æ–ª–Ω–µ–Ω–Ω—ã–µ</button>
      </div>

      <ul>
        {filteredTasks.map((task) => (
          <li key={task.id}>
            <label>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => handleToggle(task.id)}
              />
              {task.text}
            </label>
          </li>
        ))}
      </ul>
    </div>
  );
}```

