```function throttle(fn, delay, ctx) {

    let lastCall = 0
    let timer = null

    return function (...args){

        const now = Date.now()
        if (now - lastCall > delay){
            lastCall = Date.now()
            fn.call(ctx, ...args)
        } else {
            clearTimeout(timer)
            timer = setTimeout(() => {
          lastCall = Date.now()
                fn.call(ctx, ...args)
            }, delay - (now - lastCall))
        }
    }
}```
