 */
function isMonotonic(numbers) {
  let isDown = false;
  let maxNumber = Math.max(...numbers);
  for (let i = 0; i < numbers.length; i += 1) {
    const number = numbers[i];
    if (number === maxNumber && i === 0) {
      isDown = true;
    }
    if (isDown) {
      if (number < numbers[i + 1]) return false;
    }

    if (!isDown) {
      if (number > numbers[i + 1]) return false;
    }
  }
  return true;
}

