```Array.prototype.groupBy = function (cb) {
  if (!cb) {
    throw new Error('No callback added')
  }

  result = {}

  for (const item of this){
        const key = cb(item)
        if (!result[key]) {
            result[key] = []
        }
        result[key].push(item)  
  }

  return result
}

