```function convert(text){
    const arr = text.split('')
    let result = ''
    for (let i = 0; i < arr.length; i++){
        let char = arr[i];
        if (i >= 1 && char === char.toUpperCase()){
            result += '_' + char.toLowerCase()
        } else {
