```import { useEffect, useState } from 'react';

type Card = {
  id: string;
  title: string;
  description: string;
};

export function useGetCard() {
  const [cards, setCards] = useState<Card[] | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    const fetchCards = async () => {
      try {
        setIsLoading(true);
        // üîÅ –ó–¥–µ—Å—å —Ç—ã –º–æ–∂–µ—à—å –∑–∞–º–µ–Ω–∏—Ç—å –Ω–∞ fetch('/api/cards')
        await new Promise(resolve => setTimeout(resolve, 1000)); // —Å–∏–º—É–ª—è—Ü–∏—è –∑–∞–ø—Ä–æ—Å–∞

        const data: Card[] = [
          { id: '1', title: 'Card 1', description: 'First card' },
          { id: '2', title: 'Card 2', description: 'Second card' },
        ];

        setCards(data);
      } catch (err) {
        setError(err as Error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchCards();
  }, []);

  return { cards, isLoading, error };
}```

