```Array.prototype.groupBy = function (fn) {
  const result = {};

  for (const item of this) {
    const key = fn(item); // –≤—ã—á–∏—Å–ª—è–µ–º –∫–ª—é—á
    if (!result[key]) {
      result[key] = []; // –µ—Å–ª–∏ –∫–ª—é—á–∞ –Ω–µ—Ç, —Å–æ–∑–¥–∞—ë–º –º–∞—Å—Å–∏–≤
