const initialProducts: Product[] = [
  { id: 1, name: '–ö–Ω–∏–≥–∞ "React –¥–ª—è –Ω–∞—á–∏–Ω–∞—é—â–∏—Ö"', price: 29.99, category: '–ö–Ω–∏–≥–∏' },
  { id: 2, name: '–°–º–∞—Ä—Ç—Ñ–æ–Ω XYZ', price: 199.99, category: '–≠–ª–µ–∫—Ç—Ä–æ–Ω–∏–∫–∞' },
  { id: 3, name: '–ù–æ—É—Ç–±—É–∫ ABC', price: 899.99, category: '–≠–ª–µ–∫—Ç—Ä–æ–Ω–∏–∫–∞' },
  { id: 4, name: '–ö–Ω–∏–≥–∞ "–£–≥–ª—É–±–ª–µ–Ω–Ω—ã–π React"', price: 39.99, category: '–ö–Ω–∏–≥–∏' },
  { id: 5, name: '–ù–∞—É—à–Ω–∏–∫–∏', price: 59.99, category: '–ê–∫—Å–µ—Å—Å—É–∞—Ä—ã' }
